mapper.insert:


    SELECT NVL(MAX(TO_NUMBER(LINK_HST_NO)),0)+1 AS LINK_HST_NO
    FROM TB_PROV_LINK_HST

INSERT INTO TB_PROV_LINK_HST
(
	LINK_HST_NO
	,LINK_CYCLE_HR
	,LINK_KND_CD
	,LINK_DAT_YMD
	,LINK_DAT_BGIN_TM
	,LINK_DAT_END_TM
	,EXCN_DT
	,EXCN_CNT
	,SCS_YN
)
VALUES
(
	#{link_hst_no}
	, #{link_cycle_hr}
	, #{link_knd_cd}
	, #{link_dat_ymd}
	, #{link_dat_bgin_tm}
	, #{link_dat_end_tm}
	, SYSDATE
	, #{excn_cnt}
	, #{scs_yn}	
)


INSERT INTO TB_LINK_ERR_LOG
(
	ERR_LOG_NO
	,LINK_HST_NO
	,ERR_KND_CD
	,ERR_CN
	, OCRN_DT
)
VALUES
(
	(SELECT max(to_number(ERR_LOG_NO))+1 FROM TB_LINK_ERR_LOG)
	, #{linkHstNo}
	, #{err_knd_cd}
	, #{err_cn}
	, SYSDATE		
)
